(2420,'//题目链接:\"https://www.acwing.com/problem/content/2/\" \n#include<bits/stdc++.h>\nusing namespace std;\nint DP[1005][1005] = {0};\nint max(int a,int b)\n{\n	if(a>=b)\n		return a;\n	else\n		return b;\n}\nint main()\n{\n	int N,V;\n	cin>>N>>V;\n	int w[N+1],v[V+1];\n	for(int i=1;i<=N;i++)\n		cin>>v[i]>>w[i];\n	for(int i=1;i<=N;i++)\n	{\n		for(int j=1;j<=V;j++)\n		{\n			if(j<v[i])\n				DP[i][j] = DP[i-1][j];\n			else\n				DP[i][j] = max(DP[i-1][j-v[i]]+w[i],DP[i-1][j]);\n		}\n	}\n	cout<<DP[N][V];\n	return 0;\n}\n','cpp','2022-09-07 15:54:40','01背包'),
(2421,'//https://www.acwing.com/activity/content/problem/content/837/\n#include <bits/stdc++.h>\nusing namespace std;\ntypedef struct{\n  int a, b;  \n}p;\nint n;\nvector<p> segs;\nbool cmp(p x, p y){\n    if (x.a != y.a) return x.a < y.a;\n    return x.b < y.b;\n}\nvoid fun(vector<p> &segs){\n    vector<p> ans;\n    sort(segs.begin(), segs.end(), cmp);\n    p x;\n    x.a = -2e9, x.b = -2e9;\n    for (int i = 0; i < n; i++){\n        if (x.b < segs[i].a){\n            if (x.a != -2e9)    ans.push_back(x);\n            x = segs[i];\n        }\n        else{\n            x.b = max(segs[i].b, x.b);\n        }\n    }\n    ans.push_back(x);\n    segs = ans;\n}\nint main(){\n    cin >> n;\n    for (int i = 0; i < n; i++){\n        p x;\n        cin >> x.a >> x.b;\n        segs.push_back(x);\n    }\n    fun(segs);\n    cout << segs.size();\n    return 0;\n}\n','cpp','2022-09-07 15:54:41','区间合并'),
(2422,'//https://www.acwing.com/problem/content/828/\n#include <bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5 + 10;\nint e[N], ne[N], idx = 0, head = -1;\nvoid add_to_head(int x){\n    e[idx] = x;\n    ne[idx] = head;\n    head = idx;\n    idx++;\n}\nvoid add(int k, int x){\n    e[idx] = x;\n    ne[idx] = ne[k];\n    ne[k] = idx;\n    idx++;\n}\nvoid del(int k){\n    ne[k] = ne[ne[k]];\n}\nint main(){\n    int n;\n    cin >> n;\n    for (int i = 0; i < n; i++){\n        char ch;\n        cin >> ch;\n        if (ch == \'H\'){\n            int x;\n            cin >> x;\n            add_to_head(x);\n        }\n        else    if (ch == \'I\'){\n            int x, k;\n            cin >> k >> x;\n            add(k - 1, x);\n        }\n        else    if (ch == \'D\'){\n            int k;\n            cin >> k;\n            if (!k) head = ne[head];\n            del(k - 1);\n        }\n    }\n    while(head != -1){\n        cout << e[head] << \" \";\n        head = ne[head];\n    }\n    return 0;\n}\n','cpp','2022-09-07 15:54:41','单链表'),
(2423,'//https://www.acwing.com/problem/content/3764/\n#include <bits/stdc++.h>\nusing namespace std;\nconst int N = 2e5;\nint a[N + 5], r[N + 5];\nint main(){\n	int t;\n	scanf(\"%d\", &t);\n	while (t--){\n		int n;\n		scanf(\"%d\", &n);\n		int r[n];\n		memset(a, 0, (n + 1) * 4);\n		for (int i = 0; i < n; i ++){\n			scanf(\"%d\", &r[i]);\n			a[r[i]]++;\n		} \n		int res = -1;\n		for (int i = 0; i < n; i ++){\n			if (a[r[i]] == 1){\n				if (res == -1 || r[res] > r[i])	res = i;\n			}\n		}\n		if (res != -1)	res ++;\n		printf(\"%d\\n\",res);\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:42','唯一最小数'),
(2424,'//https://www.acwing.com/problem/content/789/\n#include <bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint n;\nint a[N + 5],tmp[N + 5];\nvoid merge_sort(int a[], int l, int r){\n	if (l >= r)	return;\n	int mid = l + r >> 1;\n	merge_sort(a, l , mid);\n	merge_sort(a, mid + 1, r);\n	int k = 0, i = l, j = mid + 1;\n	while (i <= mid && j <= r){\n		if (a[i] <= a[j])	tmp[k ++ ] = a[i ++ ];\n		else	tmp[k ++ ] = a[j ++ ];\n	} \n	while (i <= mid)	tmp[k ++ ] = a[i ++ ];\n	while (j <= r)	tmp[k ++ ] = a[j ++ ];\n	for (int i = l, j = 0; i <= r; i ++, j ++)	a[i] = tmp[j];\n}\nint main(){\n	scanf(\"%d\", &n);\n	for (int i = 0; i < n; i ++)	scanf(\"%d\", &a[i]);\n	merge_sort(a, 0, n - 1);\n	for (int i = 0; i < n; i ++)	printf(\"%d \", a[i]);\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','归并排序'),
(2425,'//https://www.acwing.com/problem/content/877/\n#include<bits/stdc++.h>\nusing namespace std;\nlong long pow(long long a,long long b,long long c){\n	long long ans = 1,base = a;\n	base = base % c;\n	while(b){\n		if(b&1){\n			ans = (ans*base)%c;\n		}\n		b>>=1;\n		base = (base*base)%c;\n	}\n	return ans;\n}\nint main(){\n	int n;\n	cin>>n;\n	for(int i=0;i<n;i++){\n		long long a,b,c;\n		cin>>a>>b>>c;\n		cout<<pow(a,b,c)<<endl;\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','快速幂'),
(2426,'//https://www.acwing.com/problem/content/description/792/\n#include <bits/stdc++.h> \nusing namespace std;\nint main(){\n	double n;\n	scanf(\"%lf\", &n);\n	double l = 0, r = n;\n	if (n < 0)	swap(l, r);\n	if (n > -1 && n < 1){\n		l = -1, r = 1;\n	}\n	while (r - l > 1e-9){\n		double mid = (l + r) / 2;\n		if (mid * mid * mid > n)	r = mid;\n		else	l = mid;\n	}\n	printf(\"%.6lf\", l);\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','数的三次方根'),
(2427,'//https://www.acwing.com/problem/content/870/\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e6;\nbool isPrime[N+5];\nint Prime[N+5];\nint cnt = 0;\nint len = 0;\nvoid GetPrime(int n){\n	memset(isPrime,1,sizeof(isPrime));\n	isPrime[1] = 0;\n	for(int i = 2;i<=n;i++){\n		if(isPrime[i]){\n			Prime[++cnt] = i;\n			len++;\n		}\n		for(int j=1;j<=cnt&&i*Prime[j]<=n;j++){\n			isPrime[i*Prime[j]] = 0;\n			if(i%Prime[j]==0)	break;\n		}\n	}\n}\nint main(){\n	int n;\n	cin>>n;\n	GetPrime(n);\n	cout<<len<<endl;\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','筛质数'),
