(2430,'//https://www.acwing.com/problem/content/795/\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint main() {\n	string s1,s2;\n	int a[N+5],b[N+5],c[N+10];//c存放结果\n	int i,j;\n	memset(a,0,sizeof(a));\n	memset(b,0,sizeof(b));\n	memset(c,0,sizeof(c));\n	cin>>s1>>s2;\n	a[0]=s1.length();\n	b[0]=s2.length();\n	for(int i=1; i<=a[0]; i++) {\n		a[i] = s1[a[0]-i]-\'0\';\n	}\n	for(int i=1; i<=b[0]; i++) {\n		b[i] = s2[b[0]-i]-\'0\';\n	}\n	//按位处理\n	for(int i = 1; i<=a[0]; i++) {\n		for(int j=1; j<=b[0]; j++) {\n			c[i+j-1]+=a[i]*b[j];\n			c[i+j]+=c[i+j-1]/10;\n			c[i+j-1]%=10;\n		}\n	}\n	int len = a[0]+b[0]+1;\n	while((c[len]==0&&(len>1)))len--;//去掉最高位的零\n	for(int i=len; i>=1; i--) {\n		cout<<c[i];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','高精度乘法'),
(2431,'//https://www.acwing.com/problem/content/793/\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint main(){\n	int a[N+5];\n	int b[N+5];\n	memset(a,0,sizeof(a));\n	memset(b,0,sizeof(b));\n	string s1,s2;\n	cin>>s1>>s2;\n	a[0] = s1.length();\n	b[0] = s2.length();\n	int len;\n	for(int i=1;i<=a[0];i++)	a[i] = s1[a[0]-i]-\'0\';\n	for(int i=1;i<=b[0];i++)	b[i] = s2[b[0]-i]-\'0\';\n	len = a[0]>b[0]?a[0]:b[0];\n	for(int i=1;i<=len;i++){\n		a[i]+=b[i];\n		a[i+1]+=a[i]/10;\n		a[i]%=10;\n	}\n	len++;\n	while((a[len]==0)&&(len>1))len--;\n	for(int i=len;i>=1;i--){\n		cout<<a[i];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','高精度加法'),
