(2437,'//http://182.92.86.249/problem.php?pid=1414\n#include<bits/stdc++.h>\nusing namespace std;\nbool isPrime[105];\nint Prime[105];\nint cnt = 0;\nvoid GetPrime(int n){\n	memset(isPrime,1,sizeof(isPrime));\n	isPrime[1] = 0;\n	isPrime[0] = 0;\n	for(int i=2;i<n;i++){\n		if(isPrime[i]){\n			Prime[++cnt] = i;\n		}\n		for(int j=1;j<=cnt&&i*Prime[j]<=n;j++){\n			isPrime[i*Prime[j]] = 0;\n			if(i%Prime[j]==0){\n				break;\n			}\n		}\n	}\n}\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	getchar();\n	GetPrime(101);\n	for(int i=0;i<n;i++){\n		int a[30]={0};\n		char s[105];\n		int len = 0;\n		gets(s);\n		int max = 0;\n		int min = 101;\n		for(int j=0;j<strlen(s);j++){\n			a[s[j]-\'a\']++;\n			if(a[s[j]-\'a\']>max){\n				max = a[s[j]-\'a\'];\n			}\n			if(a[j]<min){\n				min = a[s[j]-\'a\'];\n			}\n		}\n		if(isPrime[max-min]){\n			printf(\"Lucky Word\\n%d\\n\",max-min);\n		}\n		else{\n			printf(\"No Answer\\n0\\n\");\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:42','LuckyWord'),
