(2449,'//http://182.92.86.249/problem.php?pid=1059\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int a[n+5];\n	for(int i=0;i<n;i++){\n		scanf(\"%d\",&a[i]);\n	}\n	for(int i=0;i<n;i++){\n		for(int j=0;j<n-i-1;j++){\n			if(a[j]<a[j+1]){\n				int t = a[j];\n				a[j] = a[j+1];\n				a[j+1] = t;\n			}\n		}\n	}\n	for(int i=0;i<n;i++){\n		printf(\"%d \",a[i]);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]冒泡排序'),
(2450,'//http://182.92.86.249/problem.php?pid=1066\n#include<bits/stdc++.h>\nusing namespace std;\nint a[10],book[10];\nint l = 0;\nvoid dfs(int step){\n	if(step==10){\n		int num[3]={0};\n		int len = 0;\n		for(int i=1;i<=9;i++){\n			num[len]+=a[i];\n			if(i%3==0){\n				len++;\n			}\n			num[len]*=10;\n		}\n		if(num[0]+num[1]==num[2]){\n			l++;\n		}\n		return;\n	}\n	for(int i=1;i<=9;i++){\n		if(book[i]==0){\n			book[i] = 1;\n			a[step] = i;\n			dfs(step+1);\n			book[i] = 0;\n		}\n	}\n	return;\n}\nint main(){\n	dfs(1);\n	cout<<l/2;\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]坑爹的奥数'),
(2451,'//http://182.92.86.249/problem.php?pid=1061\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	set<int> s;\n	for(int i=0;i<n;i++){\n		int a;\n		scanf(\"%d\",&a);\n		s.insert(a);\n	}\n	printf(\"%d\\n\",s.size());\n	set<int>::iterator it;\n	for(it = s.begin();it!=s.end();it++){\n		printf(\"%d \",*it);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]小哼买书'),
(2452,'//http://182.92.86.249/problem.php?pid=1060\n#include<bits/stdc++.h>\nusing namespace std;\nbool cmp(int a,int b){\n	return a>b;\n}\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int a[n];\n	for(int i=0;i<n;i++){\n		cin>>a[i];\n	}\n	sort(a,a+n,cmp);\n	for(int i=0;i<n;i++){\n		cout<<a[i]<<\" \";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]快速排序'),
(2453,'//http://182.92.86.249/problem.php?pid=1069\n#include<bits/stdc++.h>\nusing namespace std;\nint a[10],book[10],n;\nvoid dfs(int step){\n	int i;\n	if(step==n+1){\n		for(i=1;i<=n;i++){\n			printf(\"%d\",a[i]);\n		}\n		printf(\"\\n\");\n		return;\n	}\n	for(i=1;i<=n;i++){\n		if(book[i]==0){\n			a[step] = i;\n			book[i] = 1;\n			dfs(step+1);\n			book[i] = 0;\n		}\n	}\n	return;\n}\nint main(){\n	scanf(\"%d\",&n);\n	dfs(1);\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]数的全排列'),
(2454,'//http://182.92.86.249/problem.php?pid=1058\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int a[1005]={0};\n	for(int i=0;i<n;i++){\n		int k;\n		scanf(\"%d\",&k);\n		a[k]++;\n	}\n	for(int i=1000;i>=0;i--){\n		for(int j=0;j<a[i];j++){\n			printf(\"%d \",i);\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]桶排序'),
(2455,'//http://123.57.166.69/problem.php?pid=1068\n#include<bits/stdc++.h>\nint num[] = {6,2,5,5,4,5,6,3,7,6};\nint fun(int N) {\n	char s[20];\n	int n = 0;\n	sprintf(s,\"%d\",N);\n	for(int i=0; i<strlen(s); i++) {\n		n+=num[s[i]-\'0\'];\n	}\n	return n;\n}\nint main() {\n	int n;\n	for(int q=0; q<=24; q++) {\n		int k = 0;\n		for(int i=0; i<=1111; i++) {\n			for(int j=0; j<=1111; j++) {\n				int c = i+j;\n				if(fun(i)+fun(j)+fun(c)==q-4) {\n					k++;\n				}\n			}\n		}\n		printf(\"%d \",q);\n		printf(\"%d\\n\",k);\n	}\n\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]火柴棍等式'),
(2456,'//http://182.92.86.249/problem.php?pid=1067\n#include<bits/stdc++.h>\nusing namespace std;\nchar a[55][55];\nint main(){\n	int n,m;\n	cin>>n>>m;\n	for(int i=1;i<=n;i++){\n		for(int j=1;j<=m;j++){\n			scanf(\"%c\",&a[i][j]);			\n		}\n		getchar();\n	}\n	int max = 0;\n	for(int i=1;i<=n;i++){\n		for(int j=1;j<=m;j++){\n			int s = 0;\n			if(a[i][j]==\'.\'){\n				int x = i;\n				int y = j;\n				while(1){\n					x++;\n					if(a[x][y]==\'G\'){\n						s++;\n					}\n					if(a[x][y]==\'#\'){\n						break;\n					}\n				}\n				x = i;\n				y = j;\n				while(1){\n					x--;\n					if(a[x][y]==\'G\'){\n						s++;\n					}\n					if(a[x][y]==\'#\'){\n						break;\n					}\n				}\n				x = i;\n				y = j;\n				while(1){\n					y++;\n					if(a[x][y]==\'G\'){\n						s++;\n					}\n					if(a[x][y]==\'#\'){\n						break;\n					}\n				}\n				x = i;\n				y = j;\n				while(1){\n					y--;\n					if(a[x][y]==\'G\'){\n						s++;\n					}\n					if(a[x][y]==\'#\'){\n						break;\n					}\n				}\n			}\n			if(max<s){\n				max = s;\n			}\n		}\n	}\n	printf(\"%d\",max);\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]炸弹人'),
(2457,'//http://182.92.86.249/problem.php?pid=1062\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	cin>>n;\n	queue<int> q;\n	for(int i=0;i<n;i++){\n		int a;\n		cin>>a;\n		q.push(a);\n	}\n	while(q.size()!=0){\n		int a = q.front();\n		cout<<a<<\" \";\n		q.pop();\n		a = q.front();\n		q.push(a);\n		q.pop(); \n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]解密QQ号（队列）'),
(2458,'//http://182.92.86.249/problem.php?pid=1063\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char s[105];\n	gets(s);\n	stack<char> head;\n	stack<char> read;\n	for(int i=0,j=strlen(s)-1;i<strlen(s);i++,j--){\n		head.push(s[i]);\n		read.push(s[j]);\n	}\n	int flag;\n	for(int i=0;i<strlen(s)/2;i++){\n		char a = head.top();\n		char b = read.top();\n		if(a==b){\n			flag = 1;\n		}\n		else{\n			flag = 0;\n			break;\n		}\n		head.pop();\n		read.pop();\n	}\n	if(flag==1){\n		cout<<\"YES\";\n	}\n	else{\n		cout<<\"NO\";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]解密回文（栈）'),
(2459,'//http://123.57.166.69/problem.php?pid=1070\n#include<bits/stdc++.h>\nusing namespace std;\nint h[105][105];\nint book[105][105];\nint N[4][2] = {{0,1},{1,0},{-1,0},{0,-1}};\ntypedef struct A{\n	int x;\n	int y;\n	int step;\n}Queue;\nint main(){\n	int n,m;\n	scanf(\"%d%d\",&n,&m);\n	for(int i=1;i<=n;i++){\n		for(int j=1;j<=m;j++){\n			scanf(\"%d\",&h[i][j]);\n		}\n	}\n	Queue Q[10005];\n	int startx,starty,q,p;\n	scanf(\"%d%d%d%d\",&startx,&starty,&q,&p);\n	int head = 1;\n	int tail = 1;\n	Q[tail].x = startx;\n	Q[tail].y = starty;\n	Q[tail].step = 0;\n	tail++;\n	book[startx][starty] = 1;\n	int flag = 0;\n	while(head<tail){\n		for(int i=0;i<4;i++){\n			int tx = Q[head].x+N[i][0];\n			int ty = Q[head].y+N[i][1];\n			if(tx<1||tx>n||ty<1||ty>m)\n				continue;\n			if(h[tx][ty] == 0 && book[tx][ty] == 0){\n				book[tx][ty] = 1;\n				Q[tail].x = tx;\n				Q[tail].y = ty;\n				Q[tail].step = Q[head].step+1;\n				tail++;\n			}\n			if(tx==q&&ty==p){\n				flag = 1;\n				break;\n			}\n		}\n		if(flag==1){\n			break;\n		}\n		head++;\n	}\n	if(flag==1){\n		printf(\"%d\",Q[tail-1].step);\n	}\n	else{\n		printf(\"No Way!\");\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]解救小哈'),
(2460,'//http://182.92.86.249/problem.php?pid=1065\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	cin>>n;\n	int a[n+5];\n	for(int i=0;i<n;i++){\n		cin>>a[i];\n	}\n	cin>>a[n];\n	sort(a,a+n+1);\n	for(int i=0;i<n+1;i++){\n		cout<<a[i]<<\" \";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:43','[啊哈算法]链表'),
(2461,'//http://182.92.86.249/problem.php?pid=1355\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	char num;\n	while(scanf(\"%d\",&n)&&n!=-1){\n		getchar();\n		char a[1005];\n		char b[1005];\n		gets(a);\n		gets(b);\n		int s[300]={0};\n		int sum = 0;\n		for(int i=0;i<strlen(a);i++){\n			if(s[a[i]]==0){\n				s[a[i]] = 1;\n				sum++;\n			}\n		}\n		int NO = 0;\n		for(int i=0;i<strlen(b);i++){\n			if(s[b[i]]==1){\n				sum--;\n				s[b[i]] = 0;\n			}\n			else{\n				NO++;\n				if(NO==7){\n					break;\n				}\n			}\n		}\n		printf(\"Round %d\\n\",n);\n		if(sum==0&&NO<=7){\n			printf(\"You win.\\n\");\n		}\n		else	if(NO<7&&sum>0){\n			printf(\"You chickened out.\\n\");\n		}\n		else	if(NO==7&&sum>0){\n			printf(\"You lose.\\n\");\n		}\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:43','[算法竞赛入门经典]HangmanJudge'),
(2462,'//http://182.92.86.249/problem.php?pid=1347\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char c;\n	int flag = 0;\n	int len = 0;\n	while((c = getchar())!=\'\\n\'){\n		if(c==\'\\\"\'){\n			len++;\n		}\n		else{\n			putchar(c);\n		}\n		if(len==1&&!flag){\n			printf(\"``\");\n			len = 0;\n			flag = !flag;\n		}\n		else	if(len==1&&flag){\n			printf(\"\'\'\");\n			len = 0;\n			flag = !flag;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[算法竞赛入门经典]Tex中的引号（TexQuotes）'),
(2463,'//http://182.92.86.249/problem.php?pid=1348\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char s[] = \"`1234567890-=QWERTYUIOP{}\\\\ASDFGHJKL;\'ZXCVBNM,./\";\n	int i;\n	char c;\n	while((c = getchar())!=EOF){\n		for(i=1;s[i]&&s[i]!=c;i++);\n		if(s[i])	putchar(s[i-1]);\n		else	putchar(c);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[算法竞赛入门经典]WERTYU'),
(2464,'//http://182.92.86.249/problem.php?pid=1345\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n,m;\n	scanf(\"%d%d\",&n,&m);\n	int a[n+5];//1代表开着灯,0代表关灯 \n	for(int i=1;i<=n;i++){\n		a[i] = 1;\n	}\n	for(int i=2;i<=m;i++){\n		for(int j=2;j<=n;j++){\n			if(j%i==0){\n				a[j] = !a[j];\n			}\n		}\n	}\n	for(int i=1;i<=n;i++){\n		if(a[i]==1){\n			printf(\"%d \",i);\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[算法竞赛入门经典]开灯问题'),
(2465,'//http://182.92.86.249/problem.php?pid=1378\n#include<bits/stdc++.h>\nusing namespace std;\ntypedef long long ll;\nint main(){\n	ll a,b,c;\n	scanf(\"%lld%lld%lld\",&a,&b,&c);\n	ll ans = 0;\n	while(b){\n		if(b&1){\n			ans = (ans+a)%c;\n		}\n		a = (a*2)%c;\n		b>>=1;\n	}\n	printf(\"%lld\",ans);\n	return 0;\n} \n','cpp','2022-09-07 15:54:44','[算法竞赛进阶指南]64位整数乘法'),
(2466,'//http://182.92.86.249/problem.php?pid=1377\n#include<bits/stdc++.h>\nusing namespace std;\ntypedef long long ll;\nll pow(ll a,ll b,ll c){\n	ll ans = 1,base = a;//ans是结果,base是底数a\n	base %=c;\n	if(b==0){\n		return 1%c;\n	}\n	while(b){\n		if(b&1){\n			ans = (ans*base)%c;\n		}\n		base = (base*base)%c;\n		b>>=1;\n	}\n	return ans;\n}\nint main(){\n	ll a,b,c;\n	scanf(\"%lld%lld%lld\",&a,&b,&c);\n	printf(\"%lld\\n\",pow(a,b,c));\n	return 0;\n} \n','cpp','2022-09-07 15:54:44','[算法竞赛进阶指南]A^B'),
(2467,'//http://182.92.86.249/problem.php?pid=1547\n#include<bits/stdc++.h>\n#include<queue>\nusing namespace std;\ntypedef long long ll;\nint main(){\n	ll n;\n	scanf(\"%lld\",&n);\n	priority_queue<ll,vector<ll>,greater<ll> > q;\n	for(ll i=0;i<n;i++){\n		ll a;\n		scanf(\"%lld\",&a);\n		q.push(a);\n	}\n	ll sum = 0;\n	while(q.size()>1){\n		ll a = q.top();\n		q.pop();\n		ll b = q.top();\n		q.pop();\n		ll num = a+b;\n		sum+=num;\n		q.push(num);\n	}\n	printf(\"%lld\",sum);\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[算法竞赛进阶指南]合并果子'),
(2468,'//http://182.92.86.249/problem.php?pid=1118\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int m;\n	scanf(\"%d\",&m);\n	bool flag = true; \n	for(int i=10000;i<=99999;i++){\n		char a[10];\n		sprintf(a,\"%d\",i);\n		if(a[0]==a[4]&&a[1]==a[3]){\n			if(a[0]+a[1]+a[2]+a[3]+a[4]-5*\'0\'==m){\n				printf(\"%d\\n\",i);\n				flag = false;\n			}\n		}\n	}\n	for(int i=100000;i<=999999;i++){\n		char a[10];\n		sprintf(a,\"%d\",i);\n		if(a[0]==a[5]&&a[1]==a[4]&&a[2]==a[3]){\n			if(a[0]+a[1]+a[2]+a[3]+a[4]+a[5]-6*\'0\'==m){\n				printf(\"%d\\n\",i);\n				flag = false;\n			}\n		}\n	}\n	if(flag){\n		printf(\"-1\");\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][历届试题]回文数字'),
(2469,'//http://182.92.86.249/problem.php?pid=1098\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	cin>>n;\n	string s1 = \"A\";\n	for(int i=1;i<n;i++){\n		string s2 = s1;\n		char a = \'A\'+i;\n		s1 = s1+a+s2;\n	}\n	cout<<s1;\n	return 0;\n} \n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]FJ的字符串'),
(2470,'//http://182.92.86.249/problem.php?pid=1099\n#include<bits/stdc++.h>\n#include<queue>\nusing namespace std;\nint main(){\n	int n;\n	cin>>n;\n	priority_queue<int,vector<int>,greater<int> > q;\n	for(int i=0;i<n;i++){\n		int a;\n		scanf(\"%d\",&a);\n		q.push(a);\n	}\n	int sum = 0;\n	while(q.size()>1){\n		int a = q.top();\n		q.pop();\n		int b = q.top();\n		q.pop();\n		int num = a+b;\n		sum+=num;\n		q.push(num);\n	}\n	printf(\"%d\",sum);\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]Huffuman树'),
(2471,'//http://182.92.86.249/problem.php?pid=1086\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint Prime[N+5];\nbool isPrime[N+5];\nint cnt = 0;\nvoid GetPrime(int n){\n	memset(isPrime,1,sizeof(isPrime));\n	isPrime[1] = 0;\n	for(int i=2;i<=n;i++){\n		if(isPrime[i]){\n			Prime[++cnt] = i;\n		}\n		for(int j=1;j<=cnt&&i*Prime[j]<=n;j++){\n			isPrime[i*Prime[j]] = 0;\n			if(i%Prime[j]==0)	break;\n		}\n	}\n}\nint main(){\n	int n,m;\n	scanf(\"%d%d\",&m,&n);\n	GetPrime(n);\n	for(int i=m;i<=n;i++){\n		int j = 1;\n		int a[105];\n		int k = 0;\n		int sum = 1;\n		int num = i;\n		while(1){\n			if(num%Prime[j]==0){\n				sum*=Prime[j];\n				num/=Prime[j];\n				a[k] = Prime[j];\n				k++;\n			}\n			else{\n				j++;\n			}\n			if(sum>i||Prime[j]>i){\n				break;\n			}\n			if(sum==i){\n				printf(\"%d=\",i);\n				printf(\"%d\",a[0]);\n				for(int u=1;u<k;u++){\n					printf(\"*%d\",a[u]);\n				}\n				printf(\"\\n\");\n				break;\n			}\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]分解质因数'),
(2472,'//http://182.92.86.249/problem.php?pid=1097\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	string s1,s2;\n	cin>>s1>>s2;\n	int flag = 0;\n	if(s1.length()!=s2.length()){\n		flag = 1;\n	}\n	else{\n		int i;\n		for(i=0;i<s1.length();i++){\n			if(s1[i]==s2[i]){\n				continue;\n			}\n			else{\n				if(s1[i]<=\'z\'&&s1[i]>=\'a\'){\n					s2[i]+=32;\n					if(s1[i]==s2[i]){\n						flag = 3;\n						continue;\n					}\n					else{\n						flag = 4;\n						break;\n					}\n				}\n				else{\n					s2[i]-=32;\n					if(s1[i]==s2[i]){\n						flag = 3;\n						continue;\n					}\n					else{\n						flag = 4;\n						break;\n					}\n				}\n			}\n		}\n		if(i==s1.length()&&flag==0){\n			flag = 2;\n		}\n	}\n	cout<<flag<<endl;\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]字符串对比'),
(2473,'//http://182.92.86.249/problem.php?pid=1085\n#include<bits/stdc++.h>\nusing namespace std;\nint main() {\n	int h,m;\n	cin>>h>>m;\n	string a[] = {\"zero\",\"one\",\"two\",\"three\",\"four\",\"five\",\"six\",\"seven\",\"eight\",\"nine\",\"ten\",\"eleven\",\"twelve\",\"thirteen\",\"fourteen\",\"fifteen\",\"sixteen\",\"seventeen\",\"eighteen\",\"nineteen\",\"twenty\"};\n	if(h<=20) {\n		cout<<a[h]<<\" \";\n	} else {\n		int k = h/10;\n		switch(k) {\n			case 2:\n				cout<<\"twenty \";\n				break;\n			case 3:\n				cout<<\"thirty \";\n				break;\n			case 4:\n				cout<<\"forty \";\n				break;\n			case 5:\n				cout<<\"fifty \";\n				break;\n		}\n		int j = h%10;\n		if(j!=0) {\n			cout<<a[j]<<\" \";\n		}\n	}\n	if(m==0) {\n		cout<<\"o\'clock\";\n	} else {\n		if(m<=20) {\n			cout<<a[m]<<\" \";\n		} else {\n			int k = m/10;\n			switch(k) {\n				case 2:\n					cout<<\"twenty \";\n					break;\n				case 3:\n					cout<<\"thirty \";\n					break;\n				case 4:\n					cout<<\"forty \";\n					break;\n				case 5:\n					cout<<\"fifty \";\n					break;\n			}\n			int j = m%10;\n			if(j!=0) {\n				cout<<a[j]<<\" \";\n			}\n		}\n	}\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]报时助手'),
