(2475,'//http://182.92.86.249/problem.php?pid=1092\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n,m;\n	cin>>n>>m;\n	int a[n+5][n+5];\n	int sum[n+5][n+5];\n	int f[n+5][n+5];\n	for(int i=0;i<n;i++){\n		for(int j=0;j<n;j++){\n			cin>>a[i][j];\n			sum[i][j] = a[i][j];\n			f[i][j] = a[i][j];\n		}\n	}\n	for(int i=0;i<m-1;i++){\n		int b = 0;\n		for(int j=0;j<n;j++){\n			int e = 0;\n			for(int k=0;k<n;k++){\n				int y = 0,c = 0,d = 0;\n				for(int u=0;u<n;u++){\n					y += f[b][c]*a[d][e];\n					c++;\n					d++;\n				}\n				sum[j][k] = y;\n				e++;\n			}\n			b++;\n		}\n		for(int j=0;j<n;j++){\n			for(int k=0;k<n;k++){\n				f[j][k] = sum[j][k];\n			}\n		}\n	}\n	for(int i=0;i<n;i++){\n		for(int j=0;j<n;j++){\n			cout<<sum[i][j]<<\" \";\n		}\n		cout<<endl;\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]矩阵乘法'),
(2476,'//http://182.92.86.249/problem.php?pid=1090\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int c[N+5];\n	int B[N+5];\n	int len;\n	B[0] = 1;\n	B[1] = 1;\n	for(int i=2;i<=n;i++){\n		char a[N+5];\n		sprintf(a,\"%d\",i);\n		int A[N+5];\n		A[0] = strlen(a);\n		for(int j=1;j<=A[0];j++){\n			A[j] = a[A[0]-j]-\'0\';\n		}\n		memset(c,0,sizeof(c));\n		for(int k=1;k<=A[0];k++){\n			for(int j=1;j<=B[0];j++){\n				c[k+j-1]+=A[k]*B[j];\n				c[k+j]+=c[k+j-1]/10;\n				c[k+j-1]%=10;\n			}\n		}\n		c[0] = A[0]+B[0]+1;\n		while((c[c[0]]==0)&&(c[0]>1))c[0]--;\n		len = c[0];\n		for(int j=0;j<=len;j++){\n			B[j] = c[j];\n		}\n	}\n	for(int i=len;i>=1;i--){\n		printf(\"%d\",c[i]);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][基础训练]阶乘计算'),
