(2484,'//http://182.92.86.249/problem.php?pid=1215\n#include<bits/stdc++.h>\nusing namespace std;\ntypedef struct{\n	int num;\n	int row;//行 \n	int col;//列 \n}Edu;\nbool cmp(Edu a,Edu b){\n	if(a.num!=b.num){\n		return a.num>b.num;\n	}\n	else	if(a.row!=b.row){\n		return a.row<b.row;\n	}\n	else	if(a.col!=b.col){\n		return a.row<b.row;\n	}\n}\nint main(){\n	Edu a[20];\n	int k = 0;\n	for(int i=0;i<3;i++){\n		for(int j=0;j<4;j++){\n			scanf(\"%d\",&a[k].num);\n			a[k].row = i+1;\n			a[k].col = j+1;\n			k++;\n		}\n	}\n	sort(a,a+12,cmp);\n	cout<<a[0].num<<\" \"<<a[0].row<<\" \"<<a[0].col;\n	return 0;\n}\n','cpp','2022-09-07 15:54:44','[蓝桥杯][算法提高]数组输出'),
