(2489,'//http://182.92.86.249/problem.php?pid=1204\n#include<bits/stdc++.h>\n#include<iterator>\nusing namespace std;\nint main(){\n	int n;\n	map<string,int> m;\n	cin>>n;\n	getchar();\n	for(int i=0;i<n;i++){\n		string s;\n		getline(cin,s);\n		m[s]++;\n	}\n	string s[n];\n	int max = 0;\n	for(auto it = m.begin();it!=m.end();it++){\n		int num = it->second;\n		if(num > max){\n			max = num;\n		}\n	}\n	int i = 0;\n	for(auto it = m.begin();it!=m.end();it++){\n		if(it->second==max){\n			s[i] = it->first;\n			i++;\n		}\n	}\n	for(int j=0;j<=i;j++){\n		cout<<s[j]<<endl;\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:45','[蓝桥杯][算法提高]色盲的民主'),
(2490,'//http://182.92.86.249/problem.php?pid=1242\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint Prime[N+5];\nbool isPrime[N+5];\nint cnt = 0;\nvoid GetPrime(int n) {\n	memset(isPrime,1,sizeof(isPrime));\n	isPrime[1] = 0;\n	for(int i=2; i<=n; i++) {\n		if(isPrime[i]) {\n			Prime[++cnt] = i;\n		}\n		for(int j=1; j<=cnt&&i*Prime[j]<=n; j++) {\n			isPrime[i*Prime[j]] = 0;\n			if(i%Prime[j]==0)	break;\n		}\n	}\n}\nint main() {\n	int n;\n	scanf(\"%d\",&n);\n	GetPrime(n);\n	int j = 1;\n	int a[105];\n	int k = 0;\n	int sum = 1;\n	int num = n;\n	while(1) {\n		if(num%Prime[j]==0) {\n			sum*=Prime[j];\n			num/=Prime[j];\n			a[k] = Prime[j];\n			k++;\n		} else {\n			j++;\n		}\n		if(sum>n||Prime[j]>n) {\n			break;\n		}\n		if(sum==n) {\n			printf(\"%d=\",n);\n			printf(\"%d\",a[0]);\n			for(int u=1; u<k; u++) {\n				printf(\"*%d\",a[u]);\n			}\n			printf(\"\\n\");\n			break;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:45','[蓝桥杯][算法提高]质因数'),
(2491,'//http://182.92.86.249/problem.php?pid=1243\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint Prime[N+5];\nbool isPrime[N+5];\nint cnt = 0;\nvoid GetPrime(int n) {\n	memset(isPrime,1,sizeof(isPrime));\n	isPrime[1] = 0;\n	for(int i=2; i<=n; i++) {\n		if(isPrime[i]) {\n			Prime[++cnt] = i;\n		}\n		for(int j=1; j<=cnt&&i*Prime[j]<=n; j++) {\n			isPrime[i*Prime[j]] = 0;\n			if(i%Prime[j]==0)	break;\n		}\n	}\n}\nint main() {\n	int n;\n	scanf(\"%d\",&n);\n	GetPrime(n);\n	int j = 1;\n	int a[105];\n	int k = 0;\n	int sum = 1;\n	int num = n;\n	while(1) {\n		if(num%Prime[j]==0) {\n			sum*=Prime[j];\n			num/=Prime[j];\n			a[k] = Prime[j];\n			k++;\n		} else {\n			j++;\n		}\n		if(sum>n||Prime[j]>n) {\n			break;\n		}\n		if(sum==n) {\n			sort(a,a+k);\n			for(int u=0; u<k; u++) {\n				printf(\"%d \",a[u]);\n			}\n			printf(\"\\n\");\n			printf(\"%d\",k);\n			break;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:45','[蓝桥杯][算法提高]质因数2'),
(2492,'//http://182.92.86.249/problem.php?pid=1212\n#include<stdio.h>\nint main(void)\n{\n \n    char a, b, c, d, z;\n    double n;\n    \n    scanf(\"%c%c%c%lf%c%c\" , &a , &b , &c , &n ,&d , &z);\n    \n    printf(\"%-8d|%8.1lf|%c\", (a-\'0\')*100+(b-\'0\')*10+(c-\'0\'), n, z);\n    \n    return 0;\n} \n','cpp','2022-09-07 15:54:45','[蓝桥杯][算法提高]输入输出格式练习'),
(2493,'//http://182.92.86.249/problem.php?pid=1202\n#include<bits/stdc++.h>\nusing namespace std;\nint a[10],b[10],c[10],n;\nset<int> se;\nvoid dfs(int length){\n	if(length==n){//到达边界\n		int num = 0;\n		for(int j=0;j<n;j++){\n			num+=c[j];\n			num*=10;	\n		}\n		num/=10;\n		se.insert(num);\n		return;\n	}\n	for(int i=0;i<n;i++){\n		if(b[a[i]]>0){\n			b[a[i]]--;\n			c[length] = a[i];\n			dfs(length+1);\n			b[a[i]]++;	\n		}\n	}\n	return;\n}\nint main(){\n	char s[10];\n	scanf(\"%s\",s);\n	n = strlen(s);\n	for(int i=0;i<n;i++){\n		a[i] = s[i]-\'0\';\n	}\n	sort(a,a+n);\n	for(int i=0;i<n;i++){\n		b[a[i]]++;\n	}\n	dfs(0);\n	for(auto i = se.begin();i!=se.end();i++){\n		printf(\"%d\\n\",*i); \n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:45','[蓝桥杯][算法提高]阮小二买彩票'),
