(2497,'//http://182.92.86.249/problem.php?pid=1296\n#include<bits/stdc++.h>\nusing namespace std;\nint DP[1005][1005];\nint max(int a,int b){\n	return a>=b?a:b;\n}\nint main(){\n	int N,V;\n	cin>>V>>N;\n	int w[N+5],v[V+5];\n	for(int i=1;i<=N;i++){\n		cin>>v[i]>>w[i];\n	}\n	for(int i=1;i<=N;i++){\n		for(int j=1;j<=V;j++){\n			if(j<v[i]){\n				DP[i][j] = DP[i-1][j];\n			}\n			else{\n				DP[i][j] = max(DP[i-1][j-v[i]]+w[i],DP[i-1][j]);\n			}\n		}\n	}\n	cout<<DP[N][V];\n	return 0;\n}\n','cpp','2022-09-07 15:54:45','[蓝桥杯][算法训练]入学考试'),
