(2515,'//http://182.92.86.249/problem.php?pid=1297\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int a[n];\n	for(int i=0;i<n;i++){\n		a[i] = 1;\n	}\n	int l = n;\n	int len = 0;\n	while(l!=1){\n		for(int i=0;i<n;i++){\n			if(a[i]==1){\n				len++;\n			}\n			if(len==3){\n				a[i] = 0;\n				len = 0;\n				l--;\n			}\n		}\n	}\n	for(int i=0;i<n;i++){\n		if(a[i]==1){\n			cout<<i+1;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','[蓝桥杯][算法训练]筛选号码'),
(2516,'//http://182.92.86.249/problem.php?pid=1314\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	double m;\n	cin>>n>>m;\n	double sum = 0;\n	if(m<=40){\n		sum = n*m;\n	}\n	else	if(m>40&&m<=50){\n		sum = m*40;\n		n-=40;\n		sum = sum+n*(m*1.5);\n	}\n	else	if(m>50){\n		sum = m*40;\n		n-=40;\n		sum = sum+n*(m*1.5);\n		n-=10;\n		sum = sum+n*(m*2);\n	}\n	printf(\"%.2lf\",sum);\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','[蓝桥杯][算法训练]薪水计算'),
