(2518,'//http://182.92.86.249/problem.php?pid=1253\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int a[15],b[15];\n	int aum = 0,bum = 0;\n	for(int i=0;i<10;i++){\n		cin>>a[i]>>b[i];\n		aum+=a[i];\n		bum+=b[i];\n	}\n	cout<<aum<<\"+\"<<bum<<\"i\";\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','[蓝桥杯][算法训练]链表数据求和操作'),
(2519,'http://182.92.86.249/problem.php?pid=1278\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int c[N+5];\n	int B[N+5];\n	int len;\n	B[0] = 1;\n	B[1] = 1;\n	for(int i=2;i<=n;i++){\n		char a[N+5];\n		sprintf(a,\"%d\",i);\n		int A[N+5];\n		A[0] = strlen(a);\n		for(int j=1;j<=A[0];j++){\n			A[j] = a[A[0]-j]-\'0\';\n		}\n		memset(c,0,sizeof(c));\n		for(int k=1;k<=A[0];k++){\n			for(int j=1;j<=B[0];j++){\n				c[k+j-1]+=A[k]*B[j];\n				c[k+j]+=c[k+j-1]/10;\n				c[k+j-1]%=10;\n			}\n		}\n		c[0] = A[0]+B[0]+1;\n		while((c[c[0]]==0)&&(c[0]>1))c[0]--;\n		len = c[0];\n		for(int j=0;j<=len;j++){\n			B[j] = c[j];\n		}\n	}\n	for(int i=1;i<=len;i++){\n		if(c[i]!=0){\n			printf(\"%d\",c[i]);\n			return 0;\n		}\n	} \n	return 0;\n}\n','cpp','2022-09-07 15:54:46','[蓝桥杯][算法训练]阶乘'),
(2520,'//http://182.92.86.249/problem.php?pid=1375\n#include<bits/stdc++.h>\nusing namespace std;\nint num = 0;\nvoid down(int n){\n	if(n==0){\n		num++;\n		return;\n	}\n	if(n<0){\n		return;\n	}\n	else{\n		down(n-1);\n		down(n-2);\n		down(n-3);\n	}\n	return;\n}\nint main(){\n	int n;\n	cin>>n;\n	down(n);\n	cout<<num;\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','[递归入门]下楼问题'),
(2521,'//http://182.92.86.249/problem.php?pid=1432\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	string s;\n	cin>>s;\n	for(int i = s.size()-1;i>=0;i--){\n		cout<<s[i];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','[递归入门]字符串反转'),
(2522,'//http://182.92.86.249/problem.php?pid=1433\n#include<bits/stdc++.h>\nusing namespace std;\nint num = 0;\nvoid down(int n,int k){\n	if(n==0&&k%2==0){\n		num++;\n		return;\n	}\n	if(n<0||(n==0&&k%2==1)){\n		return;\n	}\n	down(n-1,k+1);\n	down(n-2,k+1);\n}\nint main(){\n	down(39,0);\n	cout<<num;\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','[递归入门]第39级台阶'),
