(2524,'//http://182.92.86.249/problem.php?pid=1478\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int a[105];\n	int b[105];\n	int n,m;\n	scanf(\"%d%d\",&n,&m);\n	for(int i=0;i<n;i++){\n		scanf(\"%d\",&a[i]);\n	}\n	for(int i=0;i<m;i++){\n		int s;\n		scanf(\"%d\",&s);\n		for(int j=0;j<n;j++){\n			if(a[j]==s){\n				int k;\n				for(k=0;k<i;k++){\n					if(a[j]==b[k]){\n						break;\n					}\n				}\n				if(k==i){\n					printf(\"%d \",s);\n					b[i] = s;\n					break;\n				}\n				else{\n					break;\n				}\n			}\n		}\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','两个数组的交集'),
(2525,'//http://182.92.86.249/problem.php?pid=1465\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[20],b[20];\n	cin>>a>>b;\n	int A = 0, B = 0;\n	int num = 1;\n	for(int i = strlen(a)-1;i>=0;i--){\n		A = A+(a[i]-\'0\')*num;\n		num*=2;\n	}\n	num = 1;\n	for(int i = strlen(b)-1;i>=0;i--){\n		B = B+(b[i]-\'0\')*num;\n		num*=2;\n	}\n	int sum = A+B;\n	int s[200];\n	int i = 0;\n	while(sum){\n		s[i] = sum&1;\n		sum>>=1;\n		i++;\n	}\n	for(int j=i-1;j>=0;j--){\n		cout<<s[j];\n	}\n	if(A==0&&B==0){\n		cout<<0;\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','二进制数字之和'),
