(2527,'//http://182.92.86.249/problem.php?pid=1598\n#include<bits/stdc++.h>\nusing namespace std;\nchar str[1005];\nstring s[25]; \nint main(){\n	gets(str);\n	char *p = strtok(str,\" \");\n	int i = 0;\n	while(p){\n		string STR(&p[0],&p[strlen(p)]);\n		s[i] = STR;\n		p = strtok(NULL,\" \");\n		i++;\n	}\n	sort(s,s+i);\n	for(int j=0;j<=i;j++){\n		cout<<s[j]<<\" \";\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','单词排序'),
(2528,'//http://182.92.86.249/problem.php?pid=1488\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	char str[105];\n	scanf(\"%s\",str);\n	int j = 0;\n	int num[105];\n	char s[105];\n	int len = 0;\n	for(int i=0;i<n;i++){\n		if(str[i]==\'a\'||str[i]==\'A\'||str[i]==\'e\'||str[i]==\'E\'||str[i]==\'i\'||str[i]==\'I\'||str[i]==\'o\'||str[i]==\'O\'||str[i]==\'u\'||str[i]==\'U\'){\n			s[j++] = str[i];\n			num[len++] = i;\n		}\n	}\n	int l  = 0;\n	for(int i=0;i<n;i++){\n		if(i==num[l]&&l<len){\n			str[i] = s[--j];\n			l++;\n		}	\n	}\n	printf(\"%s\",str);\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','反转字符串II'),
(2529,'//http://182.92.86.249/problem.php?pid=1469\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[1005];\n	gets(a);\n	char *p;\n	p = strtok(a,\" \");\n	while(p){\n		for(int i=strlen(p)-1;i>=0;i--){\n			printf(\"%c\",p[i]);\n		}\n		printf(\" \");\n		p = strtok(NULL,\" \");\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','反转字符串中的单词'),
(2530,'//http://182.92.86.249/problem.php?pid=1417 \n#include<bits/stdc++.h>\nusing namespace std;\nint main() {\n	char c;\n	char str[10000];\n	int i = 0;\n	while(c = getchar()) {\n		string s[10000];\n		if(c==\'\\n\'||c==EOF) {\n			str[i] = \'\\0\';\n			i = 0;\n			int len = strlen(str);\n			for(int j=0; j<len; j++) {\n				string y(&str[j],&str[len]);\n				s[j] = y;\n			}\n			sort(s,s+len);\n			for(int j=0; j<len; j++) {\n				cout<<s[j]<<endl;\n			}\n			if(c==EOF){\n				break;\n			}\n		} else {\n			str[i] = c;\n			i++;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','后缀子串排序'),
(2531,'//http://182.92.86.249/problem.php?pid=1466\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	if(n<0){\n		printf(\"no\");\n		return 0;\n	}\n	char s[10];\n	sprintf(s,\"%d\",n);\n	int l = strlen(s);\n	for(int i=0,j=strlen(s)-1;i<=l/2;i++,j--){\n		if(s[j]==s[i]){\n			continue;\n		}\n		printf(\"no\");\n		return 0;\n	}\n	printf(\"yes\");\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','回文数'),
(2532,'//http://182.92.86.249/problem.php?pid=1439\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int t;\n	cin>>t;\n	while(t--){\n		long long a,b;\n		cin>>a>>b;\n		cout<<a+b<<endl;\n		double c,d;\n		cin>>c>>d;\n		printf(\"%.1lf\\n\",c+d);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','复杂的A+B'),
(2533,'//http://182.92.86.249/problem.php?pid=1371\n#include<bits/stdc++.h>\nusing namespace std;\ntypedef struct{\n	int bigcat;\n	int smallcat;\n}Cat;\nint main(){\n	Cat cat[85];\n	cat[1].bigcat = 1;\n	cat[1].smallcat = 0;\n	for(int i=2;i<=4;i++){\n		cat[i].bigcat = 1;\n		cat[i].smallcat = cat[i-1].smallcat+1;\n	}\n	for(int i = 5;i<81;i++){\n		cat[i].bigcat = cat[i-3].bigcat+cat[i-1].bigcat;\n		cat[i].smallcat = cat[i-1].smallcat-cat[i-3].bigcat+cat[i].bigcat;\n	}\n	int a;\n	while(cin>>a&&a){\n		cout<<cat[a].bigcat+cat[a].smallcat<<endl;\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','大猫小猫'),
(2534,'//http://182.92.86.249/problem.php?pid=1595\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[20];\n	scanf(\"%s\",a);\n	int sum = 0;\n	for(int i=0;i<strlen(a);i++){\n		int num = a[i]-\'0\';\n		if(num%2==0){\n			sum+=num;\n		}\n	}\n	cout<<sum;\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','奇怪的信'),
(2535,'//http://182.92.86.249/problem.php?pid=1458\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	double a,b,c;\n	while(cin>>a>>b>>c&&a!=0){\n		double p = (a+b+c)/2;\n		double sum = sqrt(p*(p-a)*(p-b)*(p-c));\n		printf(\"%.2lf\\n\",sum);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','好奇的杨杨'),
(2536,'//http://182.92.86.249/problem.php?pid=1362\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[1005];\n	char c;\n	int j = 0;\n	while((c=getchar())!=EOF){\n		a[j] = c;\n		j++;\n	}\n	for(int i=0;i<strlen(a);i++){\n		if(a[i]==\'y\'&&a[i+1]==\'o\'&&a[i+2]==\'u\'){\n			cout<<\"we\";\n			i+=3;\n		}\n		cout<<a[i];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','字符串替换'),
(2537,'//http://182.92.86.249/problem.php?pid=1383\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int a[4];\n	for(int i=0;i<4;i++){\n		scanf(\"%d\",&a[i]);\n	}\n	set<int> s;\n	for(int i=0;i<4;i++){\n		for(int j=0;j<4;j++){\n			for(int k=0;k<4;k++){\n				for(int l=0;l<4;l++){\n					if(i!=j&&i!=k&&i!=l&&j!=k&&j!=l&&k!=l){\n						int num = a[i]*1000+a[j]*100+a[k]*10+a[l];\n						s.insert(num);\n					}\n				}\n			}\n		}\n	}\n	for(set<int>::iterator it = s.begin();it!=s.end();it++){\n		printf(\"%d \",*it);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','学姐的神秘卡片'),
(2538,'//http://182.92.86.249/problem.php?pid=1412\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	cin>>n;\n	for(int i=0;i<n;i++){\n		int a[4];\n		for(int j=0;j<4;j++){\n			cin>>a[j];\n		}\n		for(int j=0;j<4;j++){\n			int num = 0;\n			while(a[j]){\n				if(a[j]&1){\n					num++;\n				}\n				a[j]>>=1;\n			}\n			cout<<num<<\" \";\n		}\n		cout<<endl;\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:46','小A的烦恼'),
(2539,'//http://182.92.86.249/problem.php?pid=1479\n#include<bits/stdc++.h>\nusing namespace std;\nint a[105][105];\nint main() {\n	int n,m;\n	scanf(\"%d%d\",&n,&m);\n	int sum = 0;\n	for(int i=1; i<=n; i++) {\n		for(int j=1; j<=m; j++) {\n			scanf(\"%d\",&a[i][j]);\n			if(a[i][j]==1) {\n				sum++;\n			}\n		}\n	}\n	sum*=4;\n	int count = 0;\n	for(int i=1; i<=n; i++) {\n		for(int j=1; j<=m; j++) {\n			if(a[i][j]==1) {\n				if(a[i-1][j]==1)	count++;\n				if(a[i+1][j]==1)	count++;\n				if(a[i][j-1]==1)	count++;\n				if(a[i][j+1]==1)	count++;\n			}\n		}\n	}\n	printf(\"%d\",sum-count);\n	return 0;\n}\n','cpp','2022-09-07 15:54:46','岛屿的周长'),
(2540,'//http://182.92.86.249/problem.php?pid=1398\n#include<bits/stdc++.h>\n#include<queue>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	for(int i=0;i<n;i++){\n		int m;\n		scanf(\"%d\",&m);\n		priority_queue<int,vector<int>,greater<int> > q;\n		for(int j=0;j<m;j++){\n			int a;\n			scanf(\"%d\",&a);\n			q.push(a);\n		}\n		long long sum = 0;\n		while(q.size()>1){\n			int a = q.top();\n			q.pop();\n			int b = q.top();\n			q.pop();\n			int num = a+b;\n			sum+=num;\n			q.push(num);\n		}\n		printf(\"%lld\\n\",sum);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','懒省事的小明'),
(2541,'//http://182.92.86.249/problem.php?pid=1442\n#include<bits/stdc++.h>\nusing namespace std;\nint DP[1005][1005];\nint max(int a,int b){\n	return a>=b?a:b;\n}\nint main(){\n	int N,V;\n	cin>>N>>V;\n	int w[N+5],v[N+5];\n	for(int i=1;i<=N;i++){\n		cin>>w[i];\n		v[i] = 1;\n	}\n	for(int i=1;i<=N;i++){\n		for(int j=1;j<=V;j++){\n			if(j<v[i]){\n				DP[i][j] = DP[i-1][j];\n			}\n			else{\n				DP[i][j] = max(DP[i-1][j-v[i]]+w[i],DP[i-1][j]);\n			}\n		}\n	}\n	cout<<DP[N][V];\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','我全要'),
(2542,'//http://182.92.86.249/problem.php?pid=1467\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char s[10005];\n	scanf(\"%s\",s);\n	stack<char> q;\n	if(strlen(s)%2!=0){\n		cout<<\"false\";\n		return 0;\n	}\n	else{\n		for(int i=0;i<strlen(s);i++){\n			if(s[i]==\')\'){\n				if(q.size()==0){\n					cout<<\"false\";\n					return 0;\n				}\n				char c = q.top();\n				if(c==\'(\'){\n					q.pop();\n				}\n				else{\n					cout<<\"false\";\n					return 0;\n				}\n			}\n			else	if(s[i]==\']\'){\n				if(q.size()==0){\n					cout<<\"false\";\n					return 0;\n				}\n				char c = q.top();\n				if(c==\'[\'){\n					q.pop();\n				}\n				else{\n					cout<<\"false\";\n					return 0;\n				}\n			}\n			else	if(s[i]==\'}\'){\n				if(q.size()==0){\n					cout<<\"false\";\n					return 0;\n				}\n				char c = q.top();\n				if(c==\'{\'){\n					q.pop();\n				}\n				else{\n					cout<<\"false\";\n					return 0;\n				}\n			}\n			else{\n				q.push(s[i]);\n			}\n		}\n	}\n	if(q.size()==0){\n		cout<<\"true\";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','括号匹配'),
(2543,'//http://182.92.86.249/problem.php?pid=1589\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	cin>>n;\n	int a[n];\n	for(int i=0;i<n;i++){\n		cin>>a[i];\n	}\n	sort(a,a+n);\n	int m;\n	cin>>m;\n	for(int i=0;i<m;i++){\n		int s;\n		cin>>s;\n		cout<<a[s-1]<<endl;\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:47','排序查找'),
(2544,'//http://182.92.86.249/problem.php?pid=1593\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int num;\n	cin>>num;\n	num/=7;\n	char a[20];\n	sprintf(a,\"%d\",num);\n	cout<<a[0]<<endl;\n	return 0; \n}\n','cpp','2022-09-07 15:54:47','新年幸运数字'),
(2545,'//http://182.92.86.249/problem.php?pid=1462\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	int min = 99999;\n	scanf(\"%d\",&n);\n	for(int i=0;i<n;i++){\n		int a;\n		scanf(\"%d\",&a);\n		if(a<min){\n			min = a;\n		}\n	}\n	printf(\"%d\",min);\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','旋转数组中的最小值'),
(2546,'//http://182.92.86.249/problem.php?pid=1456\n#include<bits/stdc++.h>\nusing namespace std;\nint a[1005],b[1005];\nint add(string str1,string str2){\n	int len = 0;\n	int l = 0;\n	memset(a,0,sizeof(a));\n	memset(b,0,sizeof(b));\n	a[0] = str1.length();\n	b[0] = str2.length();\n	for(int i=1;i<=a[0];i++){\n		a[i] = str1[a[0]-i]-\'0\';\n	}\n	for(int i=1;i<=b[0];i++){\n		b[i] = str2[b[0]-i]-\'0\';\n	}\n	len = a[0]>b[0]?a[0]:b[0];\n	for(int i=1;i<=len;i++){\n		a[i]+=b[i];\n		if(a[i]>=10)	l++;\n		a[i+1]+=a[i]/10;\n		a[i]%=10;\n	}\n	return l;\n}\nint main(){\n	string str1,str2;\n	while(1){\n		cin>>str1>>str2;\n		if(str1==\"0\"&&str2==\"0\"){\n			break;\n		}\n		else{\n			int l = add(str1,str2);\n			printf(\"%d\\n\",l);\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','杨杨不开心'),
(2547,'//http://182.92.86.249/problem.php?pid=1457\n#include<bits/stdc++.h>\nusing namespace std;\ntypedef long long ll;\nint main(){\n	ll n;\n	scanf(\"%lld\",&n);\n	ll a[n];\n	for(ll i = 0;i<n;i++){\n		scanf(\"%lld\",&a[i]);\n	}\n	sort(a,a+n);\n	for(ll i=0;i<n;i+=2){\n		printf(\"%lld \",a[i]);\n	}\n	for(ll i = n-1;i>=0;i-=2){\n		printf(\"%lld \",a[i]);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','杨杨要欺负你'),
(2548,'//http://182.92.86.249/problem.php?pid=1356\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e6;\nstring Stack[N+5];\nint main(){\n	int n;\n	cin>>n;\n	int head = 0;\n	for(int i=0;i<n;i++){\n		int a;\n		cin>>a;\n		if(a==1){\n			cin>>Stack[head];\n			head++;\n		}\n		else	if(a==2){\n			cout<<Stack[--head]<<endl;\n		}\n		else	if(a==3){\n			int s;\n			cin>>s;\n			cout<<Stack[s-1]<<endl;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','栈程序'),
(2549,'//http://182.92.86.249/problem.php?pid=1463\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[105];\n	scanf(\"%s\",a);\n	bool flag = false;\n	int len = 0;\n	int LEN = 0;\n	for(int i=0;i<strlen(a);i++){\n		if(a[i]<=\'Z\'&&a[i]>=\'A\'){\n			LEN++; \n		}\n		else	if(a[i]<=\'z\'&&a[i]>=\'a\'){\n			len++;\n		}\n	}\n	if(len==strlen(a)||LEN==strlen(a)||(len==strlen(a)-1)&&(a[0]<=\'Z\'&&a[0]>=\'A\')){\n		flag = true;\n	}\n	if(flag){\n		cout<<\"True\";\n	}\n	else{\n		cout<<\"False\";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','检测单词'),
(2550,'//http://182.92.86.249/problem.php?pid=1497\n#include<bits/stdc++.h>\nusing namespace std;\nstring str[105];\nint len = 0;\ntypedef struct{\n	int year = 0;\n	int month = 0;\n	int day = 0;\n	int num = 0;\n}Edu;\nbool cmp(Edu a,Edu b){\n	if(a.year!=b.year){\n		return a.year<b.year;\n	}\n	else{\n		if(a.month!=b.month){\n			return a.month<b.month;\n		}\n		else{\n			return a.day<b.day;\n		}\n	}\n}\nint main(){\n	map<string,int> ma;\n	int n;\n	int m;\n	scanf(\"%d%d\",&n,&m);\n	for(int i=0;i<n+m;i++){\n		string s;\n		cin>>s;\n		ma[s]++;\n	}\n	for(auto it = ma.begin();it!=ma.end();it++){\n		if(it->second==2){\n			str[len] = it->first;\n			len++;\n		}\n	}\n	if(len==1){\n		cout<<str[0];\n	}\n	else{\n		Edu x[len+1];\n		for(int i=0;i<len;i++){\n			for(int j=6;j<10;j++){\n				x[i].year +=str[i][j]-\'0\';\n				x[i].year*=10;\n			}\n			x[i].year/=10;\n			for(int j=10;j<12;j++){\n				x[i].month+=str[i][j]-\'0\';\n				x[i].month*=10;\n			}\n			x[i].month/=10;\n			for(int j=12;j<14;j++){\n				x[i].day+=str[i][j]-\'0\';\n				x[i].day*=10;\n			}\n			x[i].day/=10;\n			x[i].num = i;\n		}\n		sort(x,x+len,cmp);\n		cout<<str[x[0].num];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:47','毕业舞会'),
