(2584,'//https://pintia.cn/problem-sets/994805046380707840/problems/994805072641245184\n#include <bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5 + 100;\nconst int M = 1e4 + 100;\nint head, n;\nbool f[M];\ntypedef struct {\n	int val,nex,ar;\n} link;\nvector<link> v1(N), v2;\nint main() {\n	scanf(\"%d%d\", &head, &n);\n	for (int i = 0; i < n; i++) {\n		int ar;\n		scanf(\"%d\", &ar);\n		scanf(\"%d%d\", &v1[ar].val, &v1[ar].nex);\n		v1[ar].ar = ar;\n	}\n	int ar = head;\n	int lastAr, j = 0;\n	while (ar != -1){\n		int num = abs(v1[ar].val);\n		if (!f[num])	f[num] = 1, lastAr = ar;\n		else {\n			link temp;\n			temp.val = v1[ar].val;\n			temp.ar = ar;\n			temp.nex = 0;\n			v2.push_back(temp);\n			if (v2.size() > 1) {\n				v2[j].nex = temp.ar;\n				j++;\n			}\n			v1[lastAr].nex = v1[ar].nex;\n		}\n		ar = v1[ar].nex;\n	}\n	if (v2.size() != 0)	v2[v2.size() - 1].nex = -1;\n	ar = head;\n	while (ar != -1) {\n		if (v1[ar].nex == -1) {\n			printf(\"%05d %d %d\\n\", v1[ar].ar, v1[ar].val, v1[ar].nex);\n		} else {\n			printf(\"%05d %d %05d\\n\", v1[ar].ar, v1[ar].val, v1[ar].nex);\n		}\n		ar = v1[ar].nex;\n	}\n	for (int i = 0; i < v2.size(); i++) {\n		if (v2[i].nex == -1)\n			printf(\"%05d %d %d\\n\", v2[i].ar, v2[i].val, v2[i].nex);\n		else\n			printf(\"%05d %d %05d\\n\", v2[i].ar, v2[i].val, v2[i].nex);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','L2-002链表去重'),
(2585,'//https://pintia.cn/problem-sets/994805046380707840/problems/994805058485469184\n#include <bits/stdc++.h> \nusing namespace std;\ntypedef struct {\n	string name;\n	int len;\n	int sum;\n}User;\nUser user[105];\nbool cmp(User x, User y){\n	if (x.sum != y.sum)	return x.sum > y.sum;\n	else	return (x.len * 1.0 / x.sum) < (y.len * 1.0 / y.sum);\n}\nint main(){\n	int n;\n	cin >> n;\n	for (int i = 0; i < n; i ++){\n		set<int> s;\n		int m;\n		cin >> user[i].name >> user[i].len;\n		for (int j = 0; j < user[i].len; j ++){\n			int a;\n			cin >> a;\n			s.insert(a);\n		}\n		user[i].sum = s.size();\n	}\n	sort(user, user + n, cmp);\n	if (n > 3)	n = 3;\n	for (int i = 0; i < n; i ++){\n		cout << user[i].name;\n		if (i != n - 1)	cout << \" \";\n	} \n	for (int i = 0; i < 3 - n; i ++){\n		cout << \" -\";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','L2-021点赞狂魔'),
