(2591,'//https://pintia.cn/problem-sets/994805046380707840/problems/1111914599412858880\n#include<bits/stdc++.h>\nusing namespace std;\nint main() {\n	int n;\n	scanf(\"%d\",&n);\n	getchar();\n	for(int i=0; i<n; i++) {\n		string s;\n		string k[100];\n		int l = 0;\n		int L = 0;\n		int flag = 0;\n		getline(cin,s);\n		for(int j=0; j<s.length(); j++) {\n			if(s[j]==\' \') {\n				l++;\n				continue;\n			}\n			if(s[j]==\',\') {\n				L = l;\n				if(s[j-1]==\'g\'&&s[j-2]==\'n\'&&s[j-3]==\'o\') {\n					flag++;\n				} else {\n					break;\n				}\n			}\n			if(s[j]==\'.\') {\n				if(s[j-1]==\'g\'&&s[j-2]==\'n\'&&s[j-3]==\'o\') {\n					flag++;\n				} else {\n					break;\n				}\n			}\n			k[l]+=s[j];\n		}\n		if(flag==2) {\n			for(int j=0; j<=L; j++) {\n				cout<<k[j]<<\" \";\n			}\n			for(int j=L+1;j<=l-3;j++){\n				cout<<k[j]<<\" \";\n			}\n			cout<<\"qiao ben zhong.\";\n		}\n		else{\n			cout<<\"Skipped\";\n		}\n		if(i!=n-1){\n			cout<<endl;\n		}\n	}\n	return 0;\n}\n\n','cpp','2022-09-07 15:54:49','敲笨钟'),
(2592,'//题目链接:\"https://pintia.cn/problem-sets/14/problems/2990\" \n#include<bits/stdc++.h>\nusing namespace std;\nint t[31];//保存数据\nint n;//输入的数据\nint sum=0;//累加和\nint k=0;//累计行数\nint top=0;//数组的数据数量 \nvoid dfs(int N) {\n	if(sum==n){\n		cout<<n<<\"=\"<<t[0];\n		for(int i=1;i<=top-1;i++)\n			cout<<\"+\"<<t[i];\n		k++;\n		if(k%4==0||top==1)\n			cout<<endl;\n		else\n			cout<<\";\";\n	}\n	if(sum>n)\n		return;\n	for(int i=N;i<=n;i++){\n		sum+=i;\n		t[top++]=i;\n		dfs(i);\n		sum-=i;\n		top--;\n	}\n}\nint main() {\n	cin>>n;\n	dfs(1);\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','整数分解为若干项之和'),
(2593,'//https://pintia.cn/problem-sets/994805260223102976/problems/994805292322111488\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[100],b[100],c[100];\n	int t = 0;\n	int v[300]={0};\n	scanf(\"%s\",a);\n	scanf(\"%s\",b);\n	for(int i=0,j=0;i<strlen(a);i++,j++){\n		if(a[i] == b[j])\n			continue;\n		else{\n			if(a[i]<=\'z\'&&a[i]>=\'a\')\n				c[t] = a[i]-(\'a\'-\'A\');\n			else	\n				c[t] = a[i];\n			v[c[t]] = 1;\n			j--;\n			t++;\n		}\n	}\n	for(int i=0;i<t;i++){\n		if(v[c[i]]==1){\n			cout<<c[i];\n			v[c[i]]=0;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','旧键盘'),
(2594,'//题目链接:\"https://pintia.cn/problem-sets/994805260223102976/problems/1071785779399028736\" \n#include<bits/stdc++.h>\nusing namespace std;\nbool cmp(int a,int b){\n	return a>b;\n}\nint main(){\n	int n,m;\n	int a[1005]={0};\n	int sum;\n	int c[1005]={0};\n	scanf(\"%d%d\",&n,&m);\n	for(int i=0;i<m;i++){\n		for(int j=0;j<n;j++){\n			int b;\n			scanf(\"%d\",&b);\n			a[j]+=b;\n		}\n	}\n	sum = a[0];\n	int T = 0;\n	for(int i=1;i<n;i++)\n		sum = max(a[i],sum);\n	for(int i=0;i<n;i++){\n		if(sum==a[i]){\n			c[T] = i+1;\n			T++;\n		}\n	}\n	cout<<sum<<endl;\n	for(int i=0;i<T;i++){\n		if(i!=T-1)\n			cout<<c[i]<<\" \";\n		else\n			cout<<c[i];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','最好吃的月饼'),
(2595,'//https://pintia.cn/problem-sets/994805260223102976/problems/994805301562163200\n#include<bits/stdc++.h>\nusing namespace std;\ntypedef struct {\n	double a,b,c;\n} money;\nbool cmp(money A,money B) {\n	return A.c>B.c;\n}\nint main() {\n	int n,N;\n	scanf(\"%d%d\",&n,&N);\n	vector<money> A(n);\n	for(int i=0; i<n; i++) {\n		scanf(\"%lf\",&A[i].a);\n	}\n	for(int i=0; i<n; i++) {\n		scanf(\"%lf\",&A[i].b);\n	}\n	for(int i=0; i<n; i++) {\n		A[i].c = A[i].b/A[i].a;\n	}\n	sort(A.begin(),A.end(),cmp);\n	double sum = 0;\n	for(int i=0; i<n; i++) {\n		if(N<=0) {\n			break;\n		}\n		if(N>=A[i].a) {\n			sum = A[i].b + sum;\n			N = N - A[i].a;\n		} else {\n			sum = A[i].c*N + sum;\n			N = N - A[i].a;\n		}\n	}\n	printf(\"%.2lf\",sum);\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','月饼'),
(2596,'//https://pintia.cn/problem-sets/994805260223102976/problems/994805290334011392\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char M[] = {\'1\',\'0\',\'X\',\'9\',\'8\',\'7\',\'6\',\'5\',\'4\',\'3\',\'2\'};\n	int N[] = {7,9,10,5,8,4,2,1,6,3,7,9,10,5,8,4,2};\n	int n;\n	cin>>n;\n	int t=n;\n	char a[20];\n	for(int i=0;i<n;i++){\n		long long sum = 0;\n		scanf(\"%s\",a);\n		for(int j=0;j<17;j++){\n			if(a[j]<\'0\'||a[j]>\'9\'){\n				cout<<a<<endl;\n				t--;\n				goto A;\n			}\n		}\n		for(int j=0;j<17;j++){\n			sum=(a[j]-48)*N[j]+sum;	\n		}\n		sum%=11;\n		if(a[17]!=M[sum]){\n			cout<<a<<endl;\n			t--;\n			goto A;	\n		}\nA:	n = n;\n	}\n	if(t==n){\n		cout<<\"All passed\";\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:49','查验身份证'),
(2597,'//https://pintia.cn/problem-sets/994805260223102976/problems/994805308755394560\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	string E[] = {\"MON\",\"TUE\",\"WED\",\"THU\",\"FRI\",\"SAT\",\"SUN\"};\n	string a,b,c,d;\n	cin>>a>>b>>c>>d;\n	int l1,l2;\n	int flag = 1;\n	int q,w,e;\n	l1 = min(a.length(),b.length());\n	l2 = min(c.length(),d.length());\n	for(int i=0;i<l1;i++){\n		if(a[i]==b[i]&&flag){\n			if(a[i]<=\'G\'&&a[i]>=\'A\'){\n				q = a[i] - \'A\';\n				flag = 0;\n				continue;\n			}\n		}\n		if(a[i]==b[i]&&!flag){\n			if(a[i]<=\'N\'&&a[i]>=\'A\'){\n				w = a[i] - \'A\'+10;\n				break;\n			}\n			else	if(a[i]<=\'9\'&&a[i]>=\'0\'){\n				w = a[i] - \'0\';\n				break;\n			}\n		}\n	}\n	for(int i=0;i<l2;i++){\n		if(c[i]==d[i]){\n			if((c[i]<=\'z\'&&c[i]>=\'a\')||(c[i]<=\'Z\'&&c[i]>=\'A\')){\n				e = i;\n				break;\n			} \n		}\n	}\n	q%=7;\n	cout<<E[q]<<\" \";\n	printf(\"%02d:%02d\",w,e);\n	return 0;\n}\n','cpp','2022-09-07 15:54:49','福尔摩斯的约会'),
