(2600,'//题目链接:\"https://pintia.cn/problem-sets/15/problems/721\" \n#include<bits/stdc++.h>\nusing namespace std;\nconst int N=1e5;\ntypedef long long ll;\nint main()\n{\n	ll n;\n	cin >> n;\n	ll a[N + 5];\n	set<ll>s;\n	memset(a, 0, sizeof(a));\n	for (ll i = 0; i < n; i++)\n	{\n		ll b;\n		cin >> b;\n		s.insert(b);\n		a[b]++;\n	}\n	set<ll>::iterator t;\n	ll l = s.size();\n	t=s.begin(); \n	for (ll i=0;i<l;i++)\n	{\n		cout << *t << \":\" << a[*t];\n		t++;\n		if (i != l - 1)\n			cout << endl;\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:50','统计工龄'),
