(2602,'//https://pintia.cn/problem-sets/994805260223102976/problems/994805277163896832\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	int a[1005]={0};\n	scanf(\"%d\",&n); \n	for(int i=0;i<n;i++){\n		int b,c,d;\n		scanf(\"%d-%d\",&b,&c);\n		scanf(\"%d\",&d);\n		a[b]+=d;\n	}\n	int sum = 0;\n	int num = 0;\n	for(int i=0;i<1005;i++){\n		if(a[i]!=0&&a[i]>sum){\n			num = i;\n			sum = a[i];\n		}\n	}\n	cout<<num<<\" \"<<sum; \n	return 0;\n}\n','cpp','2022-09-07 15:54:50','编程团体赛'),
(2603,'//https://pintia.cn/problem-sets/994805260223102976/problems/994805280074743808\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[10005];\n	scanf(\"%s\",a);\n	int b[10]={0};\n	for(int i=0;i<strlen(a);i++){\n		if(a[i]==\'P\')\n			b[0]++;\n		if(a[i]==\'A\')\n			b[1]++;\n		if(a[i]==\'T\')\n			b[2]++;\n		if(a[i]==\'e\')\n			b[3]++;\n		if(a[i]==\'s\')\n			b[4]++;\n		if(a[i]==\'t\')\n			b[5]++;\n	}\n	while(true){\n		if(b[0]>0){\n			cout<<\"P\";\n			b[0]--;\n		}\n		if(b[1]>0){\n			cout<<\"A\";\n			b[1]--;\n		}\n		if(b[2]>0){\n			cout<<\"T\";\n			b[2]--;\n		}\n		if(b[3]>0){\n			cout<<\"e\";\n			b[3]--;\n		}\n		if(b[4]>0){\n			cout<<\"s\";\n			b[4]--;\n		}\n		if(b[5]>0){\n			cout<<\"t\";\n			b[5]--;\n		}\n		if(b[0]+b[1]+b[2]+b[3]+b[4]+b[5]==0)\n			break;\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:50','输出PATest'),
