(2605,'//https://www.luogu.com.cn/problem/P1003\n#include <bits/stdc++.h>\nusing namespace std;\nconst int N = 1e4 + 5;\nint a[N],b[N],g[N],k[N];\nint n,x , y;\nint main(){\n	scanf(\"%d\", &n);\n	for (int i = 0; i < n; i++)\n		scanf(\"%d%d%d%d\", &a[i], &b[i], &g[i], &k[i]);\n	scanf(\"%d%d\", &x, &y);\n	for (int i = n - 1; i >= 0; i--){\n		int x1 = a[i];\n		int y1 = b[i];\n		int x2 = a[i] + g[i];\n		int y2 = b[i] + k[i];\n		if (x >= x1 && x <= x2){\n			if (y >= y1 && y <= y2){\n				printf(\"%d\", i + 1);\n				return 0;\n			}\n		}\n	}\n	printf(\"-1\");\n	return 0;\n} \n','cpp','2022-09-07 15:54:50','P1003[NOIP2011提高组]铺地毯'),
(2606,'//https://www.luogu.com.cn/problem/P1012\n#include <bits/stdc++.h>\nusing namespace std;\nbool cmp(string x, string y){\n	int len = x.length() < y.length() ? x.length() : y.length();\n	for (int i = 0; i < len; i++){\n		if (x[i] != y[i])	return x[i] < y[i];\n	}\n	return len == x.length() ? x[0] < y[y.length() - 1] : y[0] < x[x.length() - 1];\n}\nint main(){\n	int n;\n	cin >> n;\n	string str[n];\n	for (int i = 0; i < n; i++)	cin >> str[i];\n	sort(str, str + n, cmp);\n	for (int i = n - 1; i >= 0; i--)	cout << str[i];\n	return 0;\n}\n','cpp','2022-09-07 15:54:50','P1012[NOIP1998提高组]拼数'),
(2607,'//https://www.luogu.com.cn/problem/P1014\n#include <bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\", &n);\n	int x = 1, y = 1;\n	for (int i = 2; i <= n; i++){\n		if ((x + y ) % 2 == 0){\n			if (x == 1)	y++;\n			else	x--,y++; \n		}\n		else{\n			if (y == 1)	x++;\n			else	x++,y--;\n		}\n	}\n	printf(\"%d/%d\", x, y);\n	return 0;\n} \n','cpp','2022-09-07 15:54:50','P1014[NOIP1999普及组]Cantor表'),
