(2610,'//https://www.luogu.com.cn/problem/P1303 \n#include<bits/stdc++.h>\nusing namespace std;\nint a[50005],b[50005],c[50005];//c存放结果\nint main() {\n	string s1,s2;\n	int i,j;\n	memset(a,0,sizeof(a));\n	memset(b,0,sizeof(b));\n	memset(c,0,sizeof(c));\n	cin>>s1>>s2;\n	a[0]=s1.length();\n	b[0]=s2.length();\n	for(int i=1; i<=a[0]; i++) {\n		a[i] = s1[a[0]-i]-\'0\';\n	}\n	for(int i=1; i<=b[0]; i++) {\n		b[i] = s2[b[0]-i]-\'0\';\n	}\n	//按位处理\n	for(int i = 1; i<=a[0]; i++) {\n		for(int j=1; j<=b[0]; j++) {\n			c[i+j-1]+=a[i]*b[j];\n			c[i+j]+=c[i+j-1]/10;\n			c[i+j-1]%=10;\n		}\n	}\n	int len = a[0]+b[0]+1;\n	while((c[len]==0&&(len>1)))len--;//去掉最高位的零\n	for(int i=len; i>=1; i--) {\n		cout<<c[i];\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:50','P1303AXBProblem'),
