(2615,'//https://www.luogu.com.cn/problem/P1598\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int a[26]={0};\n	for(int i=0;i<4;i++){\n		char s[105];\n		gets(s);\n		for(int i=0;i<strlen(s);i++){\n			a[s[i]-\'A\']++;\n		}\n	}\n	int sum = 0;\n	for(int i=0;i<26;i++){\n		if(sum<a[i]){\n			sum = a[i];\n		}\n	}\n	int SUM = sum;\n	for(int i=0;i<sum;i++){\n		for(int j=0;j<26;j++){\n			if(a[j]>=SUM){\n				printf(\"*\");\n			}\n			else\n				printf(\" \");\n			if(j!=25){\n				printf(\" \");\n			}\n		}\n		SUM--;\n		printf(\"\\n\");\n	}\n	char s = \'A\';\n	for(int i=0;i<26;i++){\n		printf(\"%c\",s);\n		s++;\n		if(i!=25){\n			printf(\" \");\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:51','P1598垂直柱状图'),
(2616,'//https://www.luogu.com.cn/record/31630194\n#include<bits/stdc++.h>\nusing namespace std;\nint a[505],b[505];\nint main(){\n	string s1,s2;\n	cin>>s1>>s2;\n	a[0] = s1.length();\n	b[0] = s2.length();\n	for(int i=1;i<=s1.length();i++)	a[i] = s1[a[0]-i]-\'0\';\n	for(int i=1;i<=s2.length();i++)	b[i] = s2[b[0]-i]-\'0\';\n	int len = s1.length()>s2.length()?s1.length():s2.length();\n	for(int i=1;i<=len;i++){\n		a[i]+=b[i];\n		a[i+1]+=a[i]/10;\n		a[i]%=10;\n	}\n	len++;\n	while((a[len]==0)&&(len>1))	len--;\n	for(int i=len;i>0;i--)	printf(\"%d\",a[i]);\n	return 0;\n} \n','cpp','2022-09-07 15:54:51','P1601A+BProblem（高精）'),
(2617,'//https://www.luogu.com.cn/problem/P2010\n#include <bits/stdc++.h>\nusing namespace std;\nbool isDate(char date[]) {\n	int year = 0, month = 0, day = 0;\n	int a[] = {0,31,29,31,30,31,30,31,31,30,31,30,31};\n	int b[] = {0,31,28,31,30,31,30,31,31,30,31,30,31};\n	for (int i = 0; i < 4; i++) {\n		year *= 10;\n		year += date[i] - \'0\';\n	}\n	for (int i = 4; i < 6; i++) {\n		month *= 10;\n		month += date[i] - \'0\';\n	}\n	for (int i = 6; i < 8; i++) {\n		day *= 10;\n		day += date[i] - \'0\';\n	}\n	if (month == 0 || day == 0)	return false;\n	if ((year % 4 == 0 && year % 100 != 0) || year % 400 == 0) {\n		if (day > a[month])	return false;\n	} else {\n		if (day > b[month])	return false;\n	}\n	return true;\n}\nbool fun(char date[]) {\n	for (int i = 0,j = 7; i < 4; i++, j--) {\n		if (date[i] != date[j])	return false;\n	}\n	return true;\n}\nint main() {\n	int start,end;\n	cin >> start >> end;\n	int num = 0;\n	FILE *fp = fopen(\"D://a.txt\", \"a\");\n	for (int i = start; i <= end; i++) {\n		char date[10];\n		sprintf(date, \"%d\", i);\n		int month = 0;\n		for (int j = 4; j < 6; j++) {\n			month *= 10;\n			month += date[j] - \'0\';\n		}\n		if (month > 12)	{\n			i += 8799;\n			continue;\n		}\n		int day = 0;\n		for (int j = 6; j < 8; j++) {\n			day *= 10;\n			day += date[j] - \'0\';\n		}\n		if (day > 31) {\n			i += 68;\n			continue;\n		}\n		if (isDate(date)) {\n			if (fun(date)) {\n				num++;\n			}\n		}\n	}\n	cout << num;\n	return 0;\n}\n','cpp','2022-09-07 15:54:51','P2010[NOIP2016普及组]回文日期'),
(2618,'//https://www.luogu.com.cn/problem/P2142\n#include<bits/stdc++.h>\nusing namespace std;\nbool cmp(string s1,string s2){\n	if(s1.size()!=s2.size())	return s1.size()>s2.size();\n	for(int i=s1.size()-1;i>=0;i--)\n		if(s1[i]!=s2[i])	return s1[i]>s2[i];\n	return true;\n}\nvector<int> sub(vector<int> a,vector<int> b){\n	vector<int> c;\n	int t = 0;\n	for(int i=0;i<a.size();i++){\n		t = a[i]-t;\n		if(i<b.size())	t-=b[i];\n		c.push_back((t+10)%10);\n		if(t<0)	t = 1;\n		else	t = 0;\n	}\n	while((c.size()>1)&&(c[c.size()-1]==0))	c.pop_back();\n	return c;\n}\nint main(){\n	string s1,s2;\n	cin>>s1>>s2;\n	vector<int> a,b,c;\n	for(int i=s1.size()-1;i>=0;i--)	a.push_back(s1[i]-\'0\');	\n	for(int i=s2.size()-1;i>=0;i--)	b.push_back(s2[i]-\'0\');\n	if(cmp(s1,s2)){\n		c = sub(a,b);\n	}\n	else{\n		printf(\"-\");\n		c = sub(b,a);\n	}\n	for(int i=c.size()-1;i>=0;i--)	printf(\"%d\",c[i]);\n	return 0;\n} \n','cpp','2022-09-07 15:54:51','P2142高精度减法'),
(2619,'//https://www.luogu.com.cn/problem/P5015\n#include <bits/stdc++.h>\nusing namespace std;\nint main(){\n	string s;\n	getline(cin, s);\n	int len = 0;\n	for (int i = 0; i < s.length(); i++)	if (s[i] != \' \')	len++;\n	cout << len;\n	return 0;\n}\n','cpp','2022-09-07 15:54:51','P5015[NOIP2018普及组]标题统计'),
(2620,'//https://www.luogu.com.cn/problem/P5594\n#include <bits/stdc++.h>\nusing namespace std;\nint a[1005][1005];\nint n,m,k;\nbool flag[1005];\nint answer[1005];\nint main(){\n	scanf(\"%d%d%d\",&n,&m,&k);\n	for (int i = 1; i <= n; i++){\n		for (int j = 1; j <= m; j++)	scanf(\"%d\", &a[i][j]);\n	}\n	for (int i = 1; i <= m; i++){\n		memset(flag, 0, sizeof(bool) * (k + 1));\n		for (int j = 1; j <= n; j++)	flag[a[j][i]] = 1;\n		for (int j = 1; j <= k; j++)	if (flag[j])	answer[j]++;\n	}\n	for (int i = 1; i <= k; i++)	printf(\"%d \", answer[i]);\n	return 0;\n} \n','cpp','2022-09-07 15:54:51','P5594【XR-4】模拟赛'),
