(2625,'//https://www.luogu.com.cn/problem/P5710\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	scanf(\"%d\",&n);\n	int flag = 0;\n	if(n%2==0){\n		flag++;\n	}\n	if(n>4&&n<=12){\n		flag++;\n	}\n	int a[4]={0};\n	switch(flag){\n		case 2:a[0]=1,a[1]=1,a[2]=0,a[3]=0;break;\n		case 1:a[1]=1,a[2]=1,a[0]=0,a[3]=0;break;\n		case 0:a[0]=0,a[1]=0,a[2]=0,a[3]=1;break;\n	} \n	for(int i=0;i<4;i++){\n		printf(\"%d \",a[i]);\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:51','P5710【深基3.例2】数的性质'),
