(2627,'//https://www.luogu.com.cn/problem/P5727\n#include<bits/stdc++.h>\nusing namespace std;\nconst int N = 1e5;\nlong long  a[N+5];\nint main(){\n	long long n;\n	scanf(\"%lld\",&n);\n	a[0] = n;\n	int len = 1;\n	while(1){\n		if(n==1){\n			break;\n		}\n		if(n%2==0){\n			n/=2;\n			a[len++] = n;\n		}\n		else{\n			n = n*3 + 1;\n			a[len++] = n;\n		}\n	}\n	for(int i=len-1;i>=0;i--){\n		printf(\"%lld \",a[i]);\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','P5727【深基5.例3】冰雹猜想'),
(2628,'//题目链接:\"https://www.luogu.org/problemnew/show/P3150\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int N;\n	cin>>N;\n	for(int i=0;i<N;i++)\n	{\n		long long a;\n		cin>>a;\n		if(((a>>1)<<1)==a)//先右移一位,再左移一位,左移时将最低位变为0,相同为偶数,不同为奇数 \n			cout<<\"pb wins\"<<endl;\n		else\n			cout<<\"zs wins\"<<endl; \n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','pb的游戏（1）'),
(2629,'//https://www.luogu.org/problem/P2550\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	int a[7];\n	int p[8]={0};\n	scanf(\"%d\",&n);\n	for(int i=0;i<7;i++)\n		scanf(\"%d\",&a[i]);\n	for(int i=0;i<n;i++){\n		int t = 0;\n		int c;\n		for(int j=0;j<7;j++){\n			scanf(\"%d\",&c);\n			for(int s = 0;s<7;s++){\n				if(c==a[s]){\n					t++;\n					break;\n				}\n			}\n		}\n		p[t]++;\n	}\n	for(int i=7;i>0;i--){\n		cout<<p[i]<<\" \";\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','[AHOI2001]彩票摇奖'),
(2630,'//https://www.luogu.org/problem/P1200\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	char a[20];\n	char b[20];\n	scanf(\"%s\",a);\n	scanf(\"%s\",b);\n	long long n = 1,m = 1;\n	for(int i=0;i<strlen(a);i++)\n		n *=a[i] - 64;\n	for(int i=0;i<strlen(b);i++)\n		m *=b[i] - 64;\n	if((n%47)==(m%47)){\n		cout<<\"GO\";\n	}\n	else{\n		cout<<\"STAY\";\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:52','[USACO1.1]你的飞碟在这儿YourRideIsHere'),
