(2633,'//题目链接:\"https://www.luogu.org/problemnew/show/P1008\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	FILE *fp = NULL;\n	for(int a=1;a<10;a++)\n	{\n		for(int b=1;b<10;b++)\n		{\n			for(int c=1;c<10;c++)\n			{\n				for(int d=1;d<10;d++)\n				{\n					for(int e=1;e<10;e++)\n					{\n						for(int f=1;f<10;f++)\n						{\n							for(int g=1;g<10;g++)\n							{\n								for(int h=1;h<10;h++)\n								{\n									for(int i=1;i<10;i++)\n									{\n										if((a!=b&&a!=c&&a!=d&&a!=e&&a!=f&&a!=g&&a!=h&&a!=i)&&(b!=c&&b!=d&&b!=e&&b!=f&&b!=g&&b!=h&&b!=i)&&(c!=d&&c!=e&&c!=f&&c!=g&&c!=h&&c!=i)&&(d!=e&&d!=f&&d!=g&&d!=h&&d!=i)&&(e!=f&&e!=g&&e!=h&&e!=i)&&(f!=g&&f!=h&&f!=i)&&(g!=h&&g!=i)&&(h!=i))\n											if((a*100+b*10+c)*2==(d*100+e*10+f)&&(a*100+b*10+c)*3==(g*100+h*10+i))\n											{\n												fp = fopen(\"D:\\\\test.txt\", \"a\");\n												fprintf(fp,\"%d%d%d %d%d%d %d%d%d\\n\",a,b,c,d,e,f,g,h,i);\n												fclose(fp);\n											}\n									}\n								}\n							}\n						}\n					}\n				}\n			}\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','三连击(暴力打表实现)'),
(2634,'//题目链接:\"https://www.luogu.org/problemnew/show/P1534\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int n;\n	cin>>n;\n	int SUM=0;\n	int sum=0;\n	for(int i=0;i<n;i++)\n	{\n		int a,b;\n		cin>>a>>b;\n		SUM=SUM+a+b-8;\n		sum+=SUM;\n	}\n	cout<<sum;\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','不高兴的津津（升级版）'),
(2635,'//题目链接:\"https://www.luogu.org/problemnew/show/P1909\" \n#include<bits/stdc++.h>\nusing namespace std;\ntypedef long long ll;\ntypedef struct\n{\n	double a,b,c;\n}str;\nbool cmp(str A,str B)\n{\n	return A.c<B.c;\n}\nint main()\n{\n	ll n;\n	cin>>n;\n	ll sum=0; \n	str s[3];\n	for(int i=0;i<3;i++)\n	{\n		cin>>s[i].a>>s[i].b;\n		if((n%(int)s[i].a)==0)\n			s[i].c=(n/(int)s[i].a)*s[i].b;\n		else\n			s[i].c=(n/(int)s[i].a+1)*s[i].b;\n	}\n	sort(s,s+3,cmp);\n	printf(\"%.0lf\",s[0].c);\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','买铅笔'),
(2636,'//https://www.luogu.org/problem/P1319\n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int N;\n	cin>>N;\n	int t = 0;\n	int flag = 0;\n	while(1){\n		if(t==N*N){\n			break;\n		}\n		int a;\n		cin>>a;\n		if(flag==0){\n			for(int i=1;i<=a;i++){\n				cout<<\"0\";\n				t++;\n				if(t%N==0){\n					cout<<endl;\n				}\n			}\n		}\n		if(flag==1){\n			for(int i=1;i<=a;i++){\n				cout<<\"1\";\n				t++;\n				if(t%N==0){\n					cout<<endl;\n				}\n			}\n		}\n		flag = !flag;\n	}\n	return 0;\n} \n','cpp','2022-09-07 15:54:52','压缩技术'),
(2637,'//题目链接:\"https://www.luogu.org/problemnew/show/P1423\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	double sum=0,s=2,n;\n	cin>>n;\n	int i=1;\n	while(1)\n	{\n		sum+=s;\n		if(sum>=n)\n			break;\n		s=s*0.98;\n		i++;\n	}\n	cout<<i;\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','小玉在游泳'),
(2638,'//题目链接:\"https://www.luogu.org/problemnew/show/P1428\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int n;\n	cin>>n;\n	int a[105];\n	for(int i=0;i<n;i++)\n		cin>>a[i];\n	int b[105]={0};\n	for(int i=0;i<n;i++)\n	{\n		for(int j=0;j<i;j++)\n			if(a[j]<a[i])\n				b[i]++;\n	}\n	for(int i=0;i<n;i++)\n		cout<<b[i]<<\' \';\n	return 0;\n} \n','cpp','2022-09-07 15:54:52','小鱼比可爱'),
(2639,'//题目链接:\"https://www.luogu.org/problemnew/show/P1424\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	long l,L;\n	long sum=0;\n	int i=0;\n	cin>>l>>L;\n	while(1)\n	{\n		if(i==L)\n			break;\n		if(l%6==0)\n		{\n			i++;\n			l++;\n			continue;\n		}\n		else	if(l%7==0)\n		{\n			i++;\n			l=1;\n			continue;\n		}\n		sum+=250;\n		i++;\n		l++;\n	}\n	cout<<sum;\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','小鱼的航程(改进版)'),
(2640,'//题目链接:\"https://www.luogu.org/problem/P4445\"\n#include<bits/stdc++.h>\ntypedef long long ll;\nusing namespace std;\nint main(){\n	long long N;\n	long long sum = 0;\n	cin>>N;\n	long long a[N];\n	cin>>a[0];\n	sum+=a[0];\n	for(long long i=1;i<N-1;i++){\n		cin>>a[i];\n		sum=sum+a[i]*2;\n		if(a[i-1]>=a[i]){\n			sum-=a[i];\n		}\n		else{\n			sum-=a[i-1];\n		}\n	}\n	cin>>a[N-1];\n	sum+=a[N-1];\n	if(a[N-1]<=a[N-2]){\n		sum-=a[N-1];\n	}\n	else{\n		sum-=a[N-2];\n	}\n	cout<<sum;\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','报名签到'),
(2641,'//题目链接:\"https://www.luogu.org/problemnew/show/P1179\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int n,m,sum=0;\n	cin>>n>>m;\n	for(int i=n;i<=m;i++)\n	{\n		char a[10];\n		sprintf(a,\"%d\",i);\n		for(int j=0;j<strlen(a);j++)\n		{\n			if(a[j]==\'2\')\n				sum++;\n		}\n	}\n	cout<<sum;\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','数字统计'),
(2642,'//题目链接:\"https://www.luogu.org/problemnew/show/P1047\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int a[10005];\n	int n;\n	cin>>n;\n	int N;\n	cin>>N;\n	for(int i=0;i<=n;i++)\n		a[i]=1;\n	int sum=0;\n	for(int i=0;i<N;i++)\n	{\n		int A,B;\n		cin>>A>>B;\n		for(int j=A;j<=B;j++)\n			if(a[j]==1)\n			{\n				a[j]=0;\n				sum++;\n			}\n	}\n	cout<<n-sum+1;\n}\n','cpp','2022-09-07 15:54:52','校门外的树'),
(2643,'//题目链接\"https://www.luogu.org/problemnew/show/P1089\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int sum=300;\n	int N=0;\n	for(int i=1;i<=12;i++)\n	{\n		int a;\n		cin>>a;\n		sum-=a;\n		if(sum<0)\n		{\n			cout<<\"-\"<<i;\n			break;\n		}\n		if(sum>=100)\n		{\n			int a=sum/100;\n			sum-=a*100;\n			N+=a*100;\n		}\n		if(i!=12)\n			sum+=300; \n		if(i==12)\n		{\n			N=N*0.2+N+sum;\n			cout<<N;\n			break;\n		}\n	}\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','津津的储蓄计划'),
(2644,'//题目链接:\"https://www.luogu.org/problemnew/show/P2141\" \n#include<bits/stdc++.h>\nusing namespace std;\nint a[105];\nint N;\nint s[105] = {0};\nint p=0;\nbool BJ1(int x)\n{\n	for(int i=0;i<p;i++)\n		if(x==s[i])\n			return false;\n	return true;\n}\nbool BJ(int x,int y)\n{\n	for(int i=0;i<N;i++)\n	{\n		if(x+y==a[i]&&BJ1(a[i]))\n		{\n			s[p] = a[i];\n			return true;	\n		}\n	}\n	return false;\n}\nint main()\n{\n	cin>>N;\n	int sum = 0;\n	for(int i=0;i<N;i++)\n		cin>>a[i];\n	for(int i=0;i<N;i++)\n	{\n		for(int j=i+1;j<N;j++)\n		{\n			if(BJ(a[i],a[j]))\n			{\n				sum++;\n				p++;\n			}\n		}\n	}\n	cout<<sum;\n	return 0;\n}\n','cpp','2022-09-07 15:54:52','珠心算测验'),
(2645,'//https://www.luogu.org/problem/P5534\n#include<bits/stdc++.h> \nusing namespace std;\nint main(){\n	int a,b,n;\n	cin>>a>>b>>n;\n	int d = b - a;\n	long long sum = 0;\n	long long c = a;\n	for(int i=0;i<n;i++){\n		sum+=c;\n		c+=d;\n	}\n	cout<<sum<<endl;\n	return 0;\n} \n','cpp','2022-09-07 15:54:52','等差数列'),
(2646,'//题目链接:\"https://www.luogu.org/problemnew/show/P1035\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int n;\n	cin>>n;\n	double sum=0,a=1;\n	while(1)\n	{\n		sum+=1/a;\n		if(sum>n)\n			break;\n		a+=1;\n	}\n	cout<<a;\n	return 0;\n}\n','cpp','2022-09-07 15:54:53','级数求和'),
(2647,'//题目链接:\"https://www.luogu.org/problemnew/show/P1567\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int N;\n	int a;\n	cin>>N>>a;\n	int sum=0,s=0;\n	for(int i=0;i<N-1;i++)\n	{\n		int b;\n		cin>>b;\n		if(b>a)\n			s++;\n		else\n		{\n			if(s>=sum)\n				sum=s;\n			s=0;\n		}\n		a=b;\n	}\n	cout<<sum+1;\n	return 0;\n}\n','cpp','2022-09-07 15:54:53','统计天数'),
(2648,'//题目链接:\"https://www.luogu.org/problemnew/show/P1980\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	long long n;\n	cin>>n;\n	int a;\n	cin>>a;\n	long long w=0;\n	for(long long i=1;i<=n;i++)\n	{\n		char s[10];\n		sprintf(s,\"%lld\",i);\n		for(long long j=0;j<strlen(s);j++)\n		{\n			if(s[j]==a+48)\n				w++;\n		}\n	}\n	cout<<w;\n	return 0;\n}\n','cpp','2022-09-07 15:54:53','计数问题'),
(2649,'//https://www.luogu.org/problem/P1116 \n#include<bits/stdc++.h>\nusing namespace std;\nint main(){\n	int n;\n	int a[10005];\n	int m = 0;\n	cin>>n;\n	for(int i=1;i<=n;i++){\n		cin>>a[i];\n	}\n	for(int i=1;i<=n-1;i++){\n		for(int j=1;j<=n-i;j++){\n			if(a[j]>a[j+1]){\n				swap(a[j],a[j+1]);\n				m++;\n			}\n		}\n	}\n	cout<<m;\n	return 0; \n}\n','cpp','2022-09-07 15:54:53','车厢重组'),
(2650,'//题目链接:\"https://www.luogu.org/problemnew/show/P1046\" \n#include<bits/stdc++.h>\nusing namespace std;\nint main()\n{\n	int a[10];\n	int n;\n	for(int i=0;i<10;i++)\n		cin>>a[i];\n	cin>>n;\n	n+=30;\n	int s=0;\n	for(int i=0;i<10;i++)\n		if(n>=a[i])\n			s++;\n	cout<<s;\n	return 0;\n}\n','cpp','2022-09-07 15:54:53','陶陶摘苹果');
